const BookCard = (props) =>{

    return(
        <div className="book read">
        <div className="cover">
          <img src={props.image}/>
        </div>
        <div className="description">
        <p class="title">Frankenstein</p>
        <span class="author">Mary Shelley</span>
        </div>
    </div>
        
    )
}

