import React, { Component } from 'react';
import SearchArea from './SearchArea';
import axios from 'axios';
import BooksList from './BooksList';

class Books extends Component {
    constructor(props) {
        super(props);
        this.state = {
            books: [],
            searchField:'',
            sort: ''
        }
    }

    // HTTP REQUEST API
    searchBook = (e) =>{
        e.preventDefault();
        axios
        .get("https://www.googleapis.com/books/v1/volumes?q=" + this.state.searchField)
        .then((data) => {
            console.log(data);
            const cleanData = this.cleanData(data)
            this.setState({ books: cleanData});
        })
    }
    
    //เก็บค่าที่ค้นหา
    handleSearch = (e) => {
        this.setState({ searchField: e.target.value })
    }
    //Sort Data
    handleSort = (e) => {
        console.log(e.target.value)
        this.setState({ sort: e.target.value })
    }
    cleanData = (data) => {
        const cleanData = data.data.items.map((book) => {
            if(book.volumeInfo.hasOwnProperty('publishedDate') === false) {
                book.volumeInfo['publishedDate'] = '0000';
            }else if(book.volumeInfo.hasOwnProperty('imageLinks') === false){
                book.volumeInfo['imageLinks'] = { thumbnail: 'https://www.google.com/url?sa=i&url=https%3A%2F%2Fen.wikipedia.org%2Fwiki%2FFile%3ANo_image_available.svg&psig=AOvVaw2JWokhnGPFR3PjasnTc_MD&ust=1631048263710000&source=images&cd=vfe&ved=0CAsQjRxqFwoTCJCy85Oe6_ICFQAAAAAdAAAAABAE'}
            }
            return book;
        })

        return cleanData;
    }

    render() {
        const sortedBooks = this.state.books.sort((a, b) =>{
            if(this.state.sort === 'Newest'){
                return parseInt(b.volumeInfo.publishedDate.substring(0, 4)) - parseInt(a.volumeInfo.publishedDate.substring(0, 4))
            }else if(this.state.sort === 'Oldest'){
                return parseInt(a.volumeInfo.publishedDate.substring(0, 4)) - parseInt(b.volumeInfo.publishedDate.substring(0, 4))
            }
        })

        return(
            <div>
                <SearchArea searchBook={this.searchBook} handleSearch={this.handleSearch} handleSort={this.handleSort} />
                <BooksList books={sortedBooks}/>
            </div>
        );
    }
}
export default Books;
