import React from 'react';

const BookCard = (props) =>{

    return(
        <div className="card-container" >
            <img src={props.image} alt=""/>
            <div className="container">
                <h2>ชื่อเรื่อง: {props.title}</h2>
                <h6>ผู้เขียน: {props.author}</h6>
                <p>{props.published === '000' ? 'Not available' : props.published.substring(0,4)}</p>
                <a href={props.previewLink} className="btn btn-primary" target="_blank" rel="noreferrer">Review</a>
            </div>
        </div>
        
    )
}

export default BookCard;


       

