import React from 'react';
import BookFavorite from './BookFavorite';
const BookCard = (props) =>{

    return(
        <div className="card-container" >
            <a href={props.previewLink} target="_blank" rel="noreferrer">
            <img src={props.image} alt=""/>
            </a>
            <div className="container">
                <h2>เรื่อง: {props.title}</h2>
                <h6>ประเภท: {props.printType}</h6>
                <h6>ผู้เขียน: {props.authors}</h6>
                <p>ปี: {props.published === '0000' ? 'Not available' : props.published.substring(0,4)}</p>
                {/* <p>ราคา: {props.listPrice === "" ? 'No Price' : props.listPrice } </p> */}
                <div className="container-right">
                    <BookFavorite
                    booksfav={this.state.booksfav}
                    delete={this.deleteToFavorite}
                    />
                </div>
                <div>
                    <ButtonComponent load={this.state.isloading} />
                </div>
            </div>
        </div>
        
    )
}

export default BookCard;


       

